import random
import requests
from bs4 import BeautifulSoup

def setHanga(hanga):
    setup = []
    for i in hanga:
        url = f'https://dic.daum.net/search.do?q={i}&dic=hanja'

        response = requests.get(url)

        try:
            html = response.text
            soup = BeautifulSoup(html, 'html.parser')
            title = soup.select_one('.sub_read')
            setup.append([i, [title.get_text().split(", ")]])
            print([i, [title.get_text().split(", ")]])
        except:
            print('크롤링 실패.. 뜻을 직접 입력해주세요: ', i)
            inp = input()
            setup.append([i, [inp.split(", ")]])
            print([i, [inp.split(", ")]])

    print(setup)

def studyHanga(hanga, start, n, type):
    question = hanga[start:start+n]
    if type == 'random':
        random.shuffle(question)
    wrong = []

    def problem(question):
        for i in question:
            print(i[0])
            if not input() in i[1]:
                wrong.append(i)
                print(f'틀렸습니다. {i}')

    problem(question)

    while wrong:
        print(f'{wrong}\n\n\n\n\n')
        random.shuffle(wrong)
        question = wrong
        wrong = []
        problem(question)

    print('끝!')

